// Helper function to decrypt private key
fn decrypt_private_key(
    encrypted_key: &str,
    password: &str,
    params: &EncryptionParams,
) -> Result<Vec<u8>, KeygenError> {
    let salt = SaltString::from_b64(&params.salt_b64)
        .map_err(|e| KeygenError::Encryption(e.to_string()))?;
    let key = generate_key(password, &salt)?;

    let nonce_bytes = BASE64
        .decode(&params.iv_b64)
        .map_err(|e| KeygenError::Encryption(e.to_string()))?;
    let nonce = Nonce::from_slice(&nonce_bytes);

    let cipher = Aes256Gcm::new(Key::<Aes256Gcm>::from_slice(&key));

    let ciphertext = BASE64
        .decode(encrypted_key)
        .map_err(|e| KeygenError::Encryption(e.to_string()))?;

    cipher
        .decrypt(nonce, ciphertext.as_ref())
        .map_err(|e| KeygenError::Encryption(e.to_string()))
}

// Test helper to generate keypair without password prompt
fn generate_test_keypair(output_path: Option<PathBuf>, password: &str) -> Result<(), KeygenError> {
    let keypair = Keypair::generate_ed25519();
    let public_key = keypair.public().encode_protobuf();
    let public_key_b58 = bs58::encode(public_key).into_string();

    let (encrypted_private_key, encryption_params) = encrypt_private_key(&keypair, password)?;

    let key_data = KeyData {
        public_key_b58: public_key_b58.clone(),
        encrypted_private_key_b64: encrypted_private_key,
        encryption_params,
    };

    let json = serde_json::to_string_pretty(&key_data)
        .map_err(|e| KeygenError::Io(std::io::Error::other(e)))?;

    let key_file_path = if let Some(path) = output_path {
        if path.is_dir() {
            path.join("config.json")
        } else {
            path
        }
    } else {
        get_key_file_path()?
    };

    fs::write(&key_file_path, json).map_err(KeygenError::Io)?;
    Ok(())
}

#[test]
fn test_key_generation_and_encryption() {
    let keypair = Keypair::generate_ed25519();
    let password = "test_password123";

    let (encrypted_key, params) = encrypt_private_key(&keypair, password).unwrap();

    assert_eq!(params.kdf, "argon2id");
    assert!(!params.salt_b64.is_empty());
    assert!(!params.iv_b64.is_empty());
    assert!(!encrypted_key.is_empty());

    // Verify we can decrypt with correct password
    let decrypted = decrypt_private_key(&encrypted_key, password, &params).unwrap();
    let original = keypair.to_protobuf_encoding().unwrap();
    assert_eq!(decrypted, original);
}

#[test]
fn test_decryption_with_wrong_password() {
    let keypair = Keypair::generate_ed25519();
    let password = "correct_password";
    let wrong_password = "wrong_password";

    let (encrypted_key, params) = encrypt_private_key(&keypair, password).unwrap();

    // Attempt decryption with wrong password should fail
    let result = decrypt_private_key(&encrypted_key, wrong_password, &params);
    assert!(result.is_err());
}

#[test]
fn test_key_file_operations() {
    let temp_dir = tempdir().unwrap();
    let output_path = temp_dir.path().join("test_config.json");

    // Test key generation and file writing
    let result = generate_test_keypair(Some(output_path.clone()), "test_password123");
    assert!(result.is_ok());

    // Verify file exists and contains valid JSON
    let contents = fs::read_to_string(&output_path).unwrap();
    let key_data: KeyData = serde_json::from_str(&contents).unwrap();

    assert!(!key_data.public_key_b58.is_empty());
    assert!(!key_data.encrypted_private_key_b64.is_empty());
    assert_eq!(key_data.encryption_params.kdf, "argon2id");
}

#[test]
fn test_invalid_directory() {
    // Test with a non-existent directory
    let result = generate_test_keypair(
        Some(PathBuf::from("/nonexistent/path/config.json")),
        "test_password123",
    );
    assert_matches!(result, Err(KeygenError::Io(_)));
}

#[test]
fn test_key_encoding() {
    let keypair = Keypair::generate_ed25519();
    let public_key = keypair.public().encode_protobuf();
    let public_key_b58 = bs58::encode(public_key).into_string();

    // Verify the public key is properly encoded
    assert!(!public_key_b58.is_empty());
    assert!(bs58::decode(&public_key_b58).into_vec().is_ok());
}

#[test]
fn test_encryption_params_serialization() {
    let params = EncryptionParams {
        kdf: "argon2id".to_string(),
        salt_b64: "test_salt".to_string(),
        iv_b64: "test_iv".to_string(),
    };

    let json = serde_json::to_string(&params).unwrap();
    let deserialized: EncryptionParams = serde_json::from_str(&json).unwrap();

    assert_eq!(params.kdf, deserialized.kdf);
    assert_eq!(params.salt_b64, deserialized.salt_b64);
    assert_eq!(params.iv_b64, deserialized.iv_b64);
}

use async_trait::async_trait;
use bitcoin::{consensus, Address, Network, Transaction};
use esplora_client::{AsyncClient, Builder};
use std::str::FromStr;
use tokio::sync::broadcast;
use tokio::time::{sleep, Duration};

#[derive(Debug)]
pub enum NodeError {
    Error(String),
}

#[async_trait]
pub trait WindowedConfirmedTransactionProvider {
    // Must only return transactions that are confirmed in the given range [min_height, max_height].
    // All returned transactions must have at least six confirmations (< current_chain_tip_height - 6).
    async fn get_confirmed_transactions(
        &self,
        address: Address,
        min_height: u32,
        max_height: u32,
    ) -> Result<Vec<Transaction>, NodeError>;

    // Must poll for new transactions and send them to the given channel.
    async fn poll_new_transactions(&self, address: Address);
}

pub struct EsploraApiClient {
    client: AsyncClient,
    tx_channel: broadcast::Sender<Transaction>,
}

impl EsploraApiClient {
    pub fn new(client: AsyncClient, capacity: usize) -> Self {
        Self {
            client,
            tx_channel: broadcast::channel(capacity).0,
        }
    }
}

#[async_trait]
impl WindowedConfirmedTransactionProvider for EsploraApiClient {
    async fn get_confirmed_transactions(
        &self,
        address: Address,
        min_height: u32,
        max_height: u32,
    ) -> Result<Vec<Transaction>, NodeError> {
        let blockchain_height = self.client.get_height().await.map_err(|e| {
            NodeError::Error(format!("Cannot retrieve height of blockchain: {}", e))
        })?;

        let new_max_height = max_height.min(blockchain_height - 6);

        let mut confirmed_txs = Vec::new();
        let mut last_seen_txid = None;

        loop {
            let address_txs = self
                .client
                .scripthash_txs(&address.script_pubkey(), last_seen_txid)
                .await
                .map_err(|e| {
                    NodeError::Error(format!("Cannot retrieve transactions for address: {}", e))
                })?;

            if address_txs.is_empty() {
                break;
            }

            let mut found_confirmed = false;
            let last_tx_height = address_txs.last().and_then(|tx| tx.status.block_height);

            for tx in address_txs {
                if let Some(block_height) = tx.status.block_height {
                    if block_height >= min_height && block_height <= new_max_height {
                        if let Ok(full_tx) = self.client.get_tx(&tx.txid).await {
                            if let Ok(bitcoin_tx) =
                                consensus::deserialize(&consensus::serialize(&full_tx.unwrap()))
                            {
                                confirmed_txs.push(bitcoin_tx);
                                found_confirmed = true;
                            }
                        }
                    }
                }
                last_seen_txid = Some(tx.txid);
            }

            if !found_confirmed && last_tx_height.is_some_and(|height| height < min_height) {
                break;
            }
        }

        Ok(confirmed_txs)
    }

    async fn poll_new_transactions(&self, address: Address) {
        let mut last_confirmed_height = match self.client.get_height().await {
            Ok(height) => height - 6,
            Err(e) => {
                eprintln!("Cannot retrieve height of blockchain: {}", e);
                return;
            }
        };

        println!(
            "Polling for new transactions for address {}, starting from confirmed height {}",
            address, last_confirmed_height
        );

        loop {
            sleep(Duration::from_secs(60)).await;

            let current_height = match self.client.get_height().await {
                Ok(height) => height,
                Err(e) => {
                    eprintln!("Cannot retrieve height of blockchain: {}", e);
                    continue;
                }
            };

            let new_confirmed_height = current_height - 6;

            if new_confirmed_height > last_confirmed_height {
                println!(
                    "New confirmed block found. From height {} to {}",
                    last_confirmed_height + 1,
                    new_confirmed_height
                );

                let new_txs = match self
                    .get_confirmed_transactions(
                        address.clone(),
                        last_confirmed_height + 1,
                        new_confirmed_height,
                    )
                    .await
                {
                    Ok(txs) => txs,
                    Err(e) => {
                        eprintln!("Error getting confirmed transactions: {:?}", e);
                        continue;
                    }
                };

                for tx in new_txs {
                    println!("Found new confirmed transaction: {}", tx.compute_txid());
                    match self.tx_channel.send(tx) {
                        Ok(_) => (),
                        Err(e) => {
                            eprintln!("Error sending transaction to channel: {:?}", e);
                        }
                    }
                }

                last_confirmed_height = new_confirmed_height;
            }
        }
    }
}

#[tokio::main]
async fn main() {
    let client = EsploraApiClient::new(
        Builder::new("https://blockstream.info/api")
            .build_async()
            .unwrap(),
        100,
    );

    let address = Address::from_str("bc1qezwz3yt46nsgzcwlg0dsw680nryjpq5u8pvzts")
        .unwrap()
        .require_network(Network::Bitcoin)
        .unwrap();

    let transactions = client
        .get_confirmed_transactions(address.clone(), 899900, 900000)
        .await
        .unwrap();
    println!("Found {} transactions.", transactions.len());
    for tx in transactions {
        println!("Transaction ID: {}", tx.compute_txid());
    }

    client.poll_new_transactions(address).await;
}

