    errors::NodeError,
    protocol::{
        block::{Block, BlockHash},
        chain_state::ChainState,
    },
};

pub struct Db {
    pub db: DB,
}

impl Db {
    pub fn new(path: &str) -> Self {
        let db = DB::open_default(path).unwrap();
        Self { db }
    }

    pub fn get_value(&self, key: &str) -> Option<String> {
        let value = self.db.get(key).unwrap();
        value.map(|v| String::from_utf8(v).unwrap())
    }

    pub fn set_value(&self, key: &str, value: &str) {
        self.db.put(key, value).unwrap();
    }

    pub fn get_block_by_height(&self, height: u64) -> Result<Option<Block>, NodeError> {
        let block_hash = self.db.get(format!("h:{}", height))?;
        if let Some(block_hash) = block_hash {
            let block = self.db.get(format!("b:{}", hex::encode(block_hash)))?;
            Ok(block.and_then(|b| Block::deserialize(&b).ok()))
        } else {
            Ok(None)
        }
    }

    pub fn get_block_by_hash(&self, hash: BlockHash) -> Result<Option<Block>, NodeError> {
        let block = self.db.get(format!("b:{}", hex::encode(hash)))?;
        Ok(block.and_then(|b| Block::deserialize(&b).ok()))
    }

    pub fn get_tip_block_hash(&self) -> Result<Option<BlockHash>, NodeError> {
        let tip = self.db.get("h:tip")?;
        Ok(tip.and_then(|b| b.as_slice().try_into().ok()))
    }

    pub fn insert_chain_state(&self, chain_state: ChainState) -> Result<(), NodeError> {
        self.db.put("c:state", chain_state.serialize()?)?;
        Ok(())
    }

    pub fn get_chain_state(&self) -> Result<Option<ChainState>, NodeError> {
        let chain_state = self.db.get("c:state")?;
        Ok(chain_state.and_then(|b| ChainState::deserialize(&b).ok()))
    }

    pub fn insert_block(&self, block: Block) -> Result<(), NodeError> {
        let block_hash = block.hash();
        self.db
            .put(format!("b:{}", hex::encode(block_hash)), block.serialize()?)
            .map_err(|e| NodeError::Error(format!("Failed to insert block: {}", e)))?;

        self.db
            .put(format!("h:{}", block.header.height), block_hash)
            .map_err(|e| NodeError::Error(format!("Failed to insert block: {}", e)))?;

        self.db.put("h:tip", block_hash)?;

        Ok(())
    }
}

use std::collections::{BTreeMap, HashSet};
use tracing::{debug, error, info, warn};

use crate::{
    errors::NodeError,
    peer_id_to_identifier,
    swarm_manager::{Network, PrivateRequest, PrivateResponse},
};
use frost_secp256k1::{
    self as frost, Identifier,
    keys::dkg::{round1, round2},
};
use libp2p::PeerId;

pub mod utils;

pub struct DkgState {
    pub dkg_started: bool,
    pub min_signers: u16,
    pub max_signers: u16,
    pub rng: frost::rand_core::OsRng,
    pub peer_id: PeerId,
    pub peers: HashSet<PeerId>,

    pub peers_to_names: BTreeMap<PeerId, String>,
    pub dkg_listeners: HashSet<PeerId>,

    pub config_file: String,

    pub start_dkg_topic: libp2p::gossipsub::IdentTopic,
    pub round1_topic: libp2p::gossipsub::IdentTopic,

    pub round1_peer_packages: BTreeMap<Identifier, round1::Package>,
    pub round2_peer_packages: BTreeMap<Identifier, round2::Package>,

    pub r1_secret_package: Option<round1::SecretPackage>,
    pub r2_secret_package: Option<round2::SecretPackage>,

    pub pubkey_package: Option<frost::keys::PublicKeyPackage>,
    pub private_key_package: Option<frost::keys::KeyPackage>,
}

impl DkgState {
    pub fn handle_dkg_start(&mut self, network_handle: &impl Network) -> Result<(), NodeError> {
        if self.dkg_started {
            debug!("DKG already started, skipping DKG process");
            return Ok(());
        }

        // Check if DKG keys already exist
        if self.private_key_package.is_some() && self.pubkey_package.is_some() {
            info!("DKG keys already exist, skipping DKG process");
            if let Some(pubkey) = &self.pubkey_package {
                info!("Existing public key: {:?}", pubkey.verifying_key());
            }
            return Ok(());
        }

        if self.dkg_listeners.len() + 1 != self.max_signers as usize {
            debug!(
                "Not all listeners have subscribed to the DKG topic, not starting DKG process. Listeners: {:?}",
                self.dkg_listeners.len()
            );
            return Ok(());
        }

        self.dkg_started = true;

        // Run the DKG initialization code
        let participant_identifier = peer_id_to_identifier(&self.peer_id);

        let (round1_secret_package, round1_package) = frost::keys::dkg::part1(
            participant_identifier,
            self.max_signers,
            self.min_signers,
            self.rng,
        )
        .expect("Failed to generate round1 package");

        self.r1_secret_package = Some(round1_secret_package);

        let round1_package_bytes = round1_package
            .serialize()
            .expect("Failed to serialize round1 package");

        // Broadcast START_DKG message to the network,
        let start_message = format!("START_DKG:{}", self.peer_id);

        match network_handle.send_broadcast(
            self.start_dkg_topic.clone(),
            start_message.as_bytes().to_vec(),
        ) {
            Ok(_) => (),
            Err(e) => {
                return Err(NodeError::Error(format!(
                    "Failed to send broadcast: {:?}",
                    e
                )));
            }
        }

        match network_handle.send_broadcast(self.round1_topic.clone(), round1_package_bytes) {
            Ok(_) => (),
            Err(e) => {
                return Err(NodeError::Error(format!(
                    "Failed to send broadcast: {:?}",
                    e
                )));
            }
        }

        match self.try_enter_round2(network_handle) {
            Ok(_) => {
                info!(
                    "Generated and published round1 package in response to DKG start signal from {}",
                    &self.peer_id
                );
                Ok(())
            }
            Err(e) => Err(NodeError::Error(format!("Failed to enter round2: {}", e))),
        }
    }

    pub fn handle_round1_payload(
        &mut self,
        network_handle: &impl Network,
        sender_peer_id: PeerId,
        package: Vec<u8>,
    ) -> Result<(), NodeError> {
        let identifier = peer_id_to_identifier(&sender_peer_id);
        let package = match frost::keys::dkg::round1::Package::deserialize(&package) {
            Ok(package) => package,
            Err(e) => {
                return Err(NodeError::Error(format!(
                    "Failed to deserialize round1 package: {}",
                    e
                )));
            }
        };
        // Add package to peer packages
        self.round1_peer_packages.insert(identifier, package);

        debug!(
            "Received round1 package from {} ({}/{})",
            sender_peer_id,
            self.round1_peer_packages.len(),
            self.max_signers - 1
        );

        self.try_enter_round2(network_handle)?;

        Ok(())
    }

    pub fn try_enter_round2(&mut self, network_handle: &impl Network) -> Result<(), NodeError> {
        if let Some(r1_secret_package) = self.r1_secret_package.as_ref() {
            if self.round1_peer_packages.len() + 1 == self.max_signers as usize {
                info!("Received all round1 packages, entering part2");
                // all packages received
                let part2_result =
                    frost::keys::dkg::part2(r1_secret_package.clone(), &self.round1_peer_packages);
                match part2_result {
                    Ok((round2_secret_package, round2_packages)) => {
                        info!("-------------------- ENTERING ROUND 2 ---------------------");
                        self.r1_secret_package = None;
                        self.r2_secret_package = Some(round2_secret_package);

                        for peer_to_send_to in self.peers.iter() {
                            let identifier = peer_id_to_identifier(peer_to_send_to);
                            let package_to_send = match round2_packages.get(&identifier) {
                                Some(package) => package,
                                None => {
                                    warn!("Round2 package not found for {}", peer_to_send_to);
                                    return Err(NodeError::Error(format!(
                                        "Round2 package not found for {}",
                                        peer_to_send_to
                                    )));
                                }
                            };

                            let request = PrivateRequest::Round2Package(package_to_send.clone());

                            match network_handle.send_private_request(*peer_to_send_to, request) {
                                Ok(_) => (),
                                Err(e) => {
                                    error!("Round2 package not found for {}", peer_to_send_to);
                                    return Err(NodeError::Error(format!(
                                        "Failed to send private request: {:?}",
                                        e
                                    )));
                                }
                            }

                            debug!("Sent round2 package to {}", peer_to_send_to);
                        }
                    }
                    Err(e) => {
                        return Err(NodeError::Error(format!("DKG round2 failed: {}", e)));
                    }
                }
            }
        }

        Ok(())
    }

    pub fn handle_round2_payload(
        &mut self,
        network_handle: &impl Network,
        sender_peer_id: PeerId,
        package: round2::Package,
        response_channel: libp2p::request_response::ResponseChannel<PrivateResponse>,
    ) -> Result<(), NodeError> {
        let identifier = peer_id_to_identifier(&sender_peer_id);

        match network_handle.send_private_response(response_channel, PrivateResponse::Pong) {
            Ok(_) => (),
            Err(e) => {
                return Err(NodeError::Error(format!(
                    "Failed to send private response: {:?}",
                    e
                )));
            }
        }

        // Add package to peer packages
        self.round2_peer_packages.insert(identifier, package);

        debug!(
            "Received round2 package from {} ({}/{})",
            sender_peer_id,
            self.round2_peer_packages.len(),
            self.max_signers - 1
        );

        if let Some(r2_secret_package) = self.r2_secret_package.as_ref() {
            if self.round2_peer_packages.len() + 1 == self.max_signers as usize {
                info!("Received all round2 packages, entering part3");
                let part3_result = frost::keys::dkg::part3(
                    &r2_secret_package.clone(),
                    &self.round1_peer_packages,
                    &self.round2_peer_packages,
                );

                match part3_result {
                    Ok((private_key_package, pubkey_package)) => {
                        info!(
                            " DKG finished successfully. Public key: {:?}",
                            pubkey_package.verifying_key()
                        );

                        self.private_key_package = Some(private_key_package);
                        self.pubkey_package = Some(pubkey_package);

                        if let Err(e) = self.save_dkg_keys(network_handle) {
                            error!("Failed to save DKG keys: {}", e);
                        } else {
                            info!("DKG keys saved to config file");
                        }

                        self.dkg_started = false;
                    }
                    Err(e) => {
                        error!("DKG failed during part3 aggregation: {}", e);
                        // Reset state so that a fresh DKG can be attempted again later
                        self.reset_dkg_state();
                    }
                }
            }
        }

        Ok(())
    }

    /// Reset DKG state after a failed run so that a new DKG round can be initiated.
    fn reset_dkg_state(&mut self) {
        self.dkg_started = false;
        self.r1_secret_package = None;
        self.r2_secret_package = None;
        self.round1_peer_packages.clear();
        self.round2_peer_packages.clear();
    }
}

use std::{
    collections::{BTreeMap, HashSet},
    fs,
    path::Path,
};

use aes_gcm::{Aes256Gcm, Key, KeyInit, Nonce, aead::Aead};
use argon2::{Argon2, password_hash::SaltString};
use base64::{Engine as _, engine::general_purpose::STANDARD as BASE64};
use frost_secp256k1::{self as frost};
use libp2p::{PeerId, gossipsub};

use crate::{
    Config, DkgKeys, EncryptionParams, KeyData, NodeError, PeerData,
    dkg::DkgState,
    protocol::block::{ChainConfig, GenesisBlock, ValidatorInfo},
    swarm_manager::{Network, PrivateRequest},
};

fn derive_key_from_password(
    password: &str,
    salt_str: &str,
) -> Result<Vec<u8>, Box<dyn std::error::Error>> {
    let argon2 = Argon2::default();
    let password_bytes = password.as_bytes();
    let salt =
        SaltString::from_b64(salt_str).map_err(|e| format!("Salt decoding failed: {}", e))?;

    let mut key = vec![0u8; 32];
    argon2
        .hash_password_into(password_bytes, salt.as_str().as_bytes(), &mut key)
        .map_err(|e| format!("Argon2 key derivation failed: {}", e))?;
    Ok(key)
}

fn encrypt_dkg_private_key(
    private_key_data: &[u8],
    password: &str,
    salt_b64: &str,
) -> Result<(String, String), Box<dyn std::error::Error>> {
    let key_bytes = derive_key_from_password(password, salt_b64)?;

    // Generate random IV
    let mut iv = [0u8; 12];
    use frost::rand_core::RngCore;
    frost::rand_core::OsRng.fill_bytes(&mut iv);
    let nonce = Nonce::from_slice(&iv);

    let cipher = Aes256Gcm::new(Key::<Aes256Gcm>::from_slice(&key_bytes));
    let ciphertext = cipher
        .encrypt(nonce, private_key_data)
        .map_err(|e| format!("AES encryption failed: {}", e))?;

    let encrypted_b64 = BASE64.encode(ciphertext);
    let iv_b64 = BASE64.encode(iv);

    Ok((encrypted_b64, iv_b64))
}

fn decrypt_dkg_private_key(
    encrypted_private_key_b64: &str,
    password: &str,
    params: &EncryptionParams,
) -> Result<Vec<u8>, Box<dyn std::error::Error>> {
    let key_bytes = derive_key_from_password(password, &params.salt_b64)?;

    let iv_bytes = BASE64.decode(&params.iv_b64)?;
    let nonce = Nonce::from_slice(&iv_bytes);

    let ciphertext = BASE64.decode(encrypted_private_key_b64)?;

    let cipher = Aes256Gcm::new(Key::<Aes256Gcm>::from_slice(&key_bytes));
    let decrypted_private_key = cipher
        .decrypt(nonce, ciphertext.as_ref())
        .map_err(|e| format!("AES decryption failed: {}", e))?;

    Ok(decrypted_private_key)
}

fn get_password_for_dkg() -> Result<String, Box<dyn std::error::Error>> {
    match std::env::var("KEY_PASSWORD") {
        Ok(pw) => Ok(pw),
        Err(_) => {
            use std::io::{self, Write};
            print!("Enter password to encrypt/decrypt DKG keys: ");
            io::stdout().flush()?;
            let password = rpassword::read_password()?;
            Ok(password)
        }
    }
}

impl DkgState {
    pub fn get_public_key(&self) -> Option<frost::keys::PublicKeyPackage> {
        self.pubkey_package.clone()
    }

    pub fn get_private_key(&self) -> Option<frost::keys::KeyPackage> {
        self.private_key_package.clone()
    }
}

impl DkgState {
    pub fn new(
        min_signers: u16,
        max_signers: u16,
        peer_id: PeerId,
        peers_to_names: BTreeMap<PeerId, String>,
        config_file: String,
    ) -> Result<Self, NodeError> {
        let mut dkg_state = DkgState {
            min_signers,
            max_signers,
            rng: frost::rand_core::OsRng,
            peer_id,
            peers_to_names,
            dkg_listeners: HashSet::new(),
            config_file,
            start_dkg_topic: gossipsub::IdentTopic::new("start-dkg"),
            round1_topic: gossipsub::IdentTopic::new("round1_topic"),
            round1_peer_packages: BTreeMap::new(),
            round2_peer_packages: BTreeMap::new(),
            r1_secret_package: None,
            r2_secret_package: None,
            pubkey_package: None,
            private_key_package: None,
            peers: HashSet::new(),
            dkg_started: false,
        };

        let keys = DkgState::load_dkg_keys(&dkg_state.config_file)
            .map_err(|e| NodeError::Error(format!("Failed to load DKG keys: {}", e)))?;

        if let Some((private_key, pubkey)) = keys {
            dkg_state.private_key_package = Some(private_key);
            dkg_state.pubkey_package = Some(pubkey);
        }

        Ok(dkg_state)
    }

    pub fn save_dkg_keys(&self, network_handle: &impl Network) -> Result<(), NodeError> {
        // Load existing config or create new one
        let mut config = if Path::new(&self.config_file).exists() {
            let config_str = fs::read_to_string(&self.config_file)
                .map_err(|e| NodeError::Error(format!("Failed to read config: {}", e)))?;
            serde_json::from_str::<Config>(&config_str)
                .map_err(|e| NodeError::Error(format!("Failed to deserialize config: {}", e)))?
        } else {
            // For new configs, we need to create a dummy key_data
            // This is not ideal but maintains compatibility with the structure
            Config {
                allowed_peers: self
                    .peers_to_names
                    .iter()
                    .map(|(peer_id, name)| PeerData {
                        name: name.clone(),
                        public_key: peer_id.to_string(),
                    })
                    .collect(),
                key_data: KeyData {
                    public_key_b58: self.peer_id.to_string(),
                    encrypted_private_key_b64: String::new(),
                    encryption_params: EncryptionParams {
                        kdf: String::new(),
                        salt_b64: String::new(),
                        iv_b64: String::new(),
                    },
                },
                dkg_keys: None,
                log_file_path: None,
            }
        };

        // Update DKG keys if they exist
        if let (Some(private_key), Some(pubkey)) = (&self.private_key_package, &self.pubkey_package)
        {
            let password = get_password_for_dkg()
                .map_err(|e| NodeError::Error(format!("Failed to get password for DKG: {}", e)))?;

            // Serialize private key to bytes
            let private_key_bytes = private_key
                .serialize()
                .map_err(|e| NodeError::Error(format!("Failed to serialize private key: {}", e)))?;

            // Use existing salt from key_data, or generate a new one if empty
            let salt_b64 = if config.key_data.encryption_params.salt_b64.is_empty() {
                // Generate a new salt
                use frost::rand_core::RngCore;
                let mut salt = [0u8; 16];
                frost::rand_core::OsRng.fill_bytes(&mut salt);
                BASE64.encode(salt)
            } else {
                config.key_data.encryption_params.salt_b64.clone()
            };

            // Encrypt the private key package
            let (encrypted_private_key_b64, iv_b64) =
                encrypt_dkg_private_key(&private_key_bytes, &password, &salt_b64).map_err(|e| {
                    NodeError::Error(format!("Failed to encrypt private key: {}", e))
                })?;

            // Serialize and base64 encode the public key package
            let pubkey_bytes = pubkey
                .serialize()
                .map_err(|e| NodeError::Error(format!("Failed to serialize public key: {}", e)))?;
            let pubkey_package_b64 = BASE64.encode(pubkey_bytes);

            config.dkg_keys = Some(DkgKeys {
                encrypted_private_key_package_b64: encrypted_private_key_b64,
                dkg_encryption_params: EncryptionParams {
                    kdf: "argon2id".to_string(),
                    salt_b64,
                    iv_b64,
                },
                pubkey_package_b64,
            });
            let validators = self
                .peers
                .iter()
                .map(|peer_id| ValidatorInfo {
                    pub_key: peer_id.to_bytes(),
                    stake: 100,
                })
                .collect();

            let chain_config = ChainConfig {
                block_time_seconds: 10,
                min_signers: self.min_signers,
                max_signers: self.max_signers,
                min_stake: 100,
                max_block_size: 1000,
            };

            let genesis_block = GenesisBlock::new(
                validators,
                chain_config,
                pubkey.serialize().map_err(|e| {
                    NodeError::Error(format!("Failed to serialize public key: {}", e))
                })?,
            );
            network_handle
                .send_self_request(
                    PrivateRequest::InsertBlock {
                        block: genesis_block.to_block(),
                    },
                    false,
                )
                .map_err(|e| NodeError::Error(format!("Failed to send genesis block: {:?}", e)))?;
        }

        // Save config
        let config_str = serde_json::to_string_pretty(&config)
            .map_err(|e| NodeError::Error(format!("Failed to serialize config: {}", e)))?;
        fs::write(&self.config_file, config_str)
            .map_err(|e| NodeError::Error(format!("Failed to write config: {}", e)))?;
        Ok(())
    }

    pub fn load_dkg_keys(
        config_path: &str,
    ) -> Result<
        Option<(frost::keys::KeyPackage, frost::keys::PublicKeyPackage)>,
        Box<dyn std::error::Error>,
    > {
        if !Path::new(config_path).exists() {
            return Ok(None);
        }

        let config_str = fs::read_to_string(config_path)?;
        let config: Config = serde_json::from_str(&config_str)?;

        if let Some(dkg_keys) = config.dkg_keys {
            let password = get_password_for_dkg()?;

            // Decrypt the private key package
            let private_key_bytes = decrypt_dkg_private_key(
                &dkg_keys.encrypted_private_key_package_b64,
                &password,
                &dkg_keys.dkg_encryption_params,
            )?;

            // Deserialize the private key from decrypted bytes
            let private_key = frost::keys::KeyPackage::deserialize(&private_key_bytes)?;

            // Deserialize the public key from base64
            let pubkey_bytes = BASE64.decode(&dkg_keys.pubkey_package_b64)?;
            let pubkey = frost::keys::PublicKeyPackage::deserialize(&pubkey_bytes)?;

            Ok(Some((private_key, pubkey)))
        } else {
            Ok(None)
        }
    }
}

