use uuid::Uuid;

pub async fn start_dkg(
    network: &NetworkHandle,
    _request: Request<StartDkgRequest>,
) -> Result<Response<StartDkgResponse>, Status> {
    // Create start-dkg topic
    let start_dkg_topic = IdentTopic::new("start-dkg");

    // Send a message to start DKG
    let start_message = "START_DKG".to_string();

    match network.send_broadcast(start_dkg_topic.clone(), start_message.as_bytes().to_vec()) {
        Ok(_) => Ok(Response::new(StartDkgResponse {
            success: true,
            message: "DKG process started".to_string(),
        })),
        Err(e) => Err(Status::internal(format!("Network error: {:?}", e))),
    }
}

pub async fn spend_funds(
    network: &NetworkHandle,
    request: Request<SpendFundsRequest>,
) -> Result<Response<SpendFundsResponse>, Status> {
    let amount_sat = request.into_inner().amount_satoshis;

    debug!("Received request to spend {} satoshis", amount_sat);
    let response = network
        .send_self_request(PrivateRequest::Spend { amount_sat }, true)
        .map_err(|e| Status::internal(format!("Network error: {:?}", e)))?
        .ok_or(Status::internal("No response from node"))?
        .await
        .map_err(|e| Status::internal(format!("Network error: {:?}", e)))?;

    let PrivateResponse::SpendRequestSent { sighash } = response else {
        return Err(Status::internal("Invalid response from node"));
    };

    Ok(Response::new(SpendFundsResponse {
        success: true,
        message: format!("Spending {} satoshis", amount_sat),
        sighash: sighash.to_string(),
    }))
}

pub async fn start_signing(
    network: &NetworkHandle,
    request: Request<StartSigningRequest>,
) -> Result<Response<StartSigningResponse>, Status> {
    let hex_msg = request.into_inner().hex_message;

    let network_request = PrivateRequest::StartSigningSession {
        hex_message: hex_msg.clone(),
    };

    let response = network
        .send_self_request(network_request, true)
        .map_err(|e| Status::internal(format!("Network error: {:?}", e)))?
        .ok_or(Status::internal("No response from node"))?
        .await
        .map_err(|e| Status::internal(format!("Network error: {:?}", e)))?;

    let PrivateResponse::StartSigningSession { sign_id } = response else {
        return Err(Status::internal(format!(
            "Invalid response from node {:?}",
            response
        )));
    };

    Ok(Response::new(StartSigningResponse {
        success: true,
        message: "Signing session started".to_string(),
        sign_id,
    }))
}

pub async fn send_direct_message(
    network: &NetworkHandle,
    request: Request<SendDirectMessageRequest>,
) -> Result<Response<SendDirectMessageResponse>, Status> {
    let req = request.into_inner();

    let target_peer_id = req
        .peer_id
        .parse::<PeerId>()
        .map_err(|e| Status::invalid_argument(format!("Invalid peer ID: {}", e)))?;

    let direct_message = format!("From: {}", req.message);

    match network.send_private_request(
        target_peer_id,
        PrivateRequest::Ping(PingBody {
            message: direct_message,
        }),
    ) {
        Ok(_) => Ok(Response::new(SendDirectMessageResponse {
            success: true,
            message: format!("Message sent to {}", target_peer_id),
        })),
        Err(e) => Err(Status::internal(format!("Network error: {:?}", e))),
    }
}

pub async fn create_deposit_intent(
    network: &NetworkHandle,
    request: Request<CreateDepositIntentRequest>,
) -> Result<Response<CreateDepositIntentResponse>, Status> {
    let req = request.into_inner();

    let user_id = req
        .user_id
        .parse::<PeerId>()
        .map_err(|e| Status::invalid_argument(format!("Invalid peer ID: {}", e)))?;

    let amount_sat = if req.amount_satoshis > 0 {
        req.amount_satoshis
    } else {
        return Err(Status::invalid_argument(
            "Amount to deposit must be greater than 0",
        ));
    };

    let deposit_tracking_id = Uuid::new_v4().to_string();

    let frost_pubkey_hex = network
        .send_self_request(PrivateRequest::GetFrostPublicKey, true)
        .map_err(|e| Status::internal(format!("Network error: {:?}", e)))?
        .ok_or(Status::internal("No response from node"))?
        .await
        .map_err(|e| Status::internal(format!("Network error: {:?}", e)))?;

    let PrivateResponse::GetFrostPublicKey { public_key } = frost_pubkey_hex else {
        return Err(Status::internal(
            "Invalid response from node. No public key found.",
        ));
    };

    let public_key = bitcoin::PublicKey::from_str(&public_key)
        .map_err(|e| Status::internal(format!("Failed to parse public key: {}", e)))?;

    let witness_script = Builder::new()
        .push_key(&public_key)
        .push_opcode(bitcoin::opcodes::all::OP_CHECKSIG)
        .into_script();

    let deposit_address = bitcoin::Address::p2wsh(&witness_script, bitcoin::Network::Testnet);

    info!(
        "Received request to create deposit intent for user {} with amount {}. Tracking ID: {}. Deposit Address: {}",
        user_id,
        amount_sat,
        deposit_tracking_id.clone(),
        deposit_address
    );

    Ok(Response::new(CreateDepositIntentResponse {
        success: true,
        message: format!("Deposit intent created for user {}", user_id),
        deposit_tracking_id,
        deposit_address: deposit_address.to_string(),
    }))
}

use std::{fs, path::PathBuf};

use crate::{Config, EncryptionParams, errors::NodeError};
use aes_gcm::{Aes256Gcm, Key, KeyInit, Nonce, aead::Aead};
use argon2::{Argon2, password_hash::SaltString};
use base64::{Engine as _, engine::general_purpose::STANDARD as BASE64};
use directories::ProjectDirs;
use libp2p::identity::Keypair;
use tracing::debug;

pub fn get_key_file_path() -> Result<PathBuf, NodeError> {
    let proj_dirs = ProjectDirs::from("", "", "TheVault")
        .ok_or_else(|| NodeError::Error("Failed to determine project directory".into()))?;

    let config_dir = proj_dirs.config_dir();
    fs::create_dir_all(config_dir)
        .map_err(|e| NodeError::Error(format!("Failed to create config directory: {}", e)))?;

    let path = config_dir.join("config.json");
    debug!("Using key file path: {}", path.display());
    Ok(path)
}

pub fn get_config_file_path(file_path_option: Option<String>) -> Result<PathBuf, NodeError> {
    if let Some(file_path_str) = file_path_option {
        let mut path = PathBuf::from(file_path_str);
        if path.is_dir() {
            path.push("config.json");
        }
        println!("Using key file path: {}", path.display());
        Ok(path)
    } else {
        let proj_dirs = ProjectDirs::from("", "", "TheVault")
            .ok_or_else(|| NodeError::Error("Failed to determine project directory".into()))?;
        let config_dir = proj_dirs.config_dir();
        Ok(config_dir.join("config.json"))
    }
}

fn derive_key_from_password(password: &str, salt_str: &str) -> Result<Vec<u8>, NodeError> {
    let argon2 = Argon2::default();
    let password_bytes = password.as_bytes();
    let salt = SaltString::from_b64(salt_str)
        .map_err(|e| NodeError::Error(format!("Salt decoding failed: {}", e)))?;

    let mut key = vec![0u8; 32];
    argon2
        .hash_password_into(password_bytes, salt.as_str().as_bytes(), &mut key)
        .map_err(|e| NodeError::Error(format!("Argon2 key derivation failed: {}", e)))?;
    Ok(key)
}

fn decrypt_private_key(
    encrypted_private_key_b64: &str,
    password: &str,
    params: &EncryptionParams,
) -> Result<Vec<u8>, NodeError> {
    let key_bytes = derive_key_from_password(password, &params.salt_b64)?;

    let iv_bytes = BASE64
        .decode(&params.iv_b64)
        .map_err(|e| NodeError::Error(format!("IV decoding failed: {}", e)))?;
    let nonce = Nonce::from_slice(&iv_bytes);

    let ciphertext = BASE64
        .decode(encrypted_private_key_b64)
        .map_err(|e| NodeError::Error(format!("Ciphertext decoding failed: {}", e)))?;

    let cipher = Aes256Gcm::new(Key::<Aes256Gcm>::from_slice(&key_bytes));

    let decrypted_private_key = cipher
        .decrypt(nonce, ciphertext.as_ref())
        .map_err(|e| NodeError::Error(format!("AES decryption failed: {}", e)))?;

    Ok(decrypted_private_key)
}

fn get_password_from_prompt() -> Result<String, NodeError> {
    rpassword::prompt_password("Enter password to decrypt identity key: ")
        .map_err(|e| NodeError::Error(e.to_string()))
}

pub fn get_config(config_filepath: Option<String>) -> Result<Config, NodeError> {
    let key_file_path = if let Some(config_path) = config_filepath {
        PathBuf::from(config_path)
    } else {
        get_key_file_path()?
    };

    debug!("Using key file path: {}", key_file_path.display());

    let config_contents = fs::read_to_string(&key_file_path)
        .map_err(|e| NodeError::Error(format!("Failed to read config file: {}", e)))?;

    debug!("Read config file");

    let config = serde_json::from_str::<Config>(&config_contents)
        .map_err(|e| NodeError::Error(format!("Failed to deserialize config file: {}", e)))?;

    debug!("Deserialized config file");

    Ok(config)
}

pub fn load_and_decrypt_keypair(config_data: &Config) -> Result<Keypair, NodeError> {
    let password = match std::env::var("KEY_PASSWORD") {
        Ok(pw) => pw,
        Err(_) => get_password_from_prompt()?,
    };

    let private_key_protobuf = decrypt_private_key(
        &config_data.key_data.encrypted_private_key_b64,
        &password,
        &config_data.key_data.encryption_params,
    )?;

    Keypair::from_protobuf_encoding(&private_key_protobuf).map_err(|e| {
        NodeError::Error(format!(
            "Failed to reconstruct keypair from protobuf: {}",
            e
        ))
    })
}

